import React, { useRef } from "react";
// import ChatWindow from "./ChatWindow";

import { Form, Input, Button, Divider } from "antd";

const layout = {
  labelCol: {
    span: 8,
  },

  wrapperCol: {
    span: 16,
  },
};

const tailLayout = {
  wrapperCol: {
    offset: 8,
    span: 16,
  },
};

export default function LoginForm(onInfoSubmit) {
  //  const nameRef = useRef();
  const idRef = useRef();

  function handleSubmit(event) {
    event.preventDefault();

    onInfoSubmit(idRef.current.value);
  }

  // if (this.state.submitted) {
  //   return (
  //     <Divider className="container">
  //       <ChatWindow
  //         className="chat-window"
  //         username={this.state.username}
  //         userId={this.state.userId}
  //       />
  //     </Divider>
  //   );
  // }

  return (
    <Divider className="login-container">
      <h1
        style={{
          fontWeight: "bold",
          fontSize: 18,
          textAlign: "center",
          display: "block",
        }}
      >
        Enter your user name and user id
      </h1>
      <br />
      <Divider className="login-form">
        <Form
          {...layout}
          initialValues={{ remember: true }}
          onSubmit={handleSubmit}
        >
          <Form.Item
            label="Id"
            name="userId"
            rules={[{ required: true, message: "Please enter your user id" }]}
          >
            <Input
              placeholder="Enter your user id"
              type="text"
              ref={idRef}
              autoComplete="off"
            />
          </Form.Item>
          <Form.Item {...tailLayout}>
            <Button type="primary" htmlType="submit" target="_blank">
              Login
            </Button>
          </Form.Item>
        </Form>
      </Divider>
    </Divider>
  );
}
