import React, { useState } from "react";

import { Divider, Button, Form, Input } from "antd";
import { SendOutlined } from "@ant-design/icons";

import "../../css/Conversation.css";

export default function Conversation({ userId }) {
  const [text, setText] = useState("");
  const [messages, setMessages] = useState([]);

  console.log(messages);

  function sendMessage(event) {
    setMessages([...messages, text]);
    setText("");
  }

  return (
    <Divider className="conversation-pane">
      {/*For testing*/}
      <Divider className="messages-container">
        {userId}
        {messages.map((message) => (
          <Divider key={message.toString()}>{message}</Divider>
        ))}
      </Divider>
      {/**/}

      <Form id="message-input" onFinish={sendMessage}>
        <Form.Item rules={[{ required: true }]}>
          <Input.TextArea
            placeholder="Enter a message..."
            value={text}
            onChange={(e) => setText(e.target.value)}
            style={{
              height: "20px",
              width: "100%",
              resize: "none",
              radius: "20px",
              display: "inline",
            }}
          />
        </Form.Item>
        <Form.Item name="sendButton">
          <Button
            className="sendButton"
            type="primary"
            disabled={!text}
            htmlType="submit"
            style={{
              size: "middle",
              shape: "circle",
              backgroundColor: "00BFFF",
            }}
            icon={<SendOutlined />}
          />
        </Form.Item>
      </Form>
    </Divider>
  );
}
