import LoginForm from "./components/LoginForm";
import useLocalStorage from "./hooks/useLocalStorage";

function App() {
  const [id, setId] = useState();

  const chatWindow = <ChatWindow id={id} />;

  return id ? chatWindow : <LoginForm onIdSubmit={setId} />;
}

export default App;
